package com.ngockieubao.orderapp.utilimport android.widget.ImageViewimport android.widget.Toastimport androidx.databinding.BindingAdapterimport com.bumptech.glide.Glide@BindingAdapter("setUrl")fun ImageView.setUrl(url: String?) {    try {        Glide.with(this.context).load(url).into(this)    } catch (ex: Exception) {        Toast.makeText(this.context, "Ex: $ex", Toast.LENGTH_SHORT).show()    }}