package com.ngockieubao.orderapp.utilimport java.text.SimpleDateFormatimport java.util.*object Utils {    fun formatTime(time: Date): String {        val timeFormat = SimpleDateFormat("HH:mm:ss", Locale.getDefault())        return timeFormat.format(time)    }    fun formatDate(date: Date): String {        val dateFormat = SimpleDateFormat("EEE-dd-MM-yyyy", Locale.getDefault())        return dateFormat.format(date)    }}