package com.ngockieubao.orderapp.utilimport com.ngockieubao.orderapp.data.Orderimport java.text.DecimalFormatimport java.text.NumberFormatimport java.text.SimpleDateFormatimport java.util.*import kotlin.math.floorobject Utils {    fun formatTime(time: Date): String {        val timeFormat = SimpleDateFormat("HH:mm:ss", Locale.getDefault())        return timeFormat.format(time)    }    fun formatDate(date: Date): String {        val dateFormat = SimpleDateFormat("dd-MM-yyyy", Locale.getDefault())        return dateFormat.format(date)    }    fun formatPrice(input: Double): String {        val formatter: NumberFormat = DecimalFormat("#,###")        val formattedNumber: String = formatter.format(input)        return "$formattedNumber VNĐ"    }    fun roundSold(input: Int): String {        val res = input.div(10).toDouble()        return "${floor(res).toInt().times(10)}+"    }    fun formatListOrder(name: String, price: Double, quantity: Int): String {        return "- $name (${formatPrice(price)}) - Số lượng: $quantity"    }    fun stringArrToList(input: List<Order>): String {        val size = input.size        var result = ""        for (i in 0 until size) {            val res = formatListOrder(input[i].name, input[i].price, input[i].quantity)            result += "$res\n"        }        return result    }    fun formatQuantity(input: Int): String {        return "Số lượng: $input"    }    fun formatWelcome(input: String): String {        return "Xin chào, $input"    }}